const animateCSS=(element,animation,prefix="animate__")=>new Promise((resolve,reject)=>{const animationName=`${prefix}${animation}`;const node=document.querySelector(element);node.classList.add(`${prefix}animated`,animationName);function handleAnimationEnd(event){event.stopPropagation();node.classList.remove(`${prefix}animated`,animationName);resolve("Animation ended")}node.addEventListener("animationend",handleAnimationEnd,{once:true})});const eventBasedOnDevice=navigator.userAgent.match(/ipod|ipad|iphone/i)?"touchstart":"click";document.getElementById("homepageLogo").addEventListener(eventBasedOnDevice,function event(event){animateHomepageLogo()});function animateHomepageLogo(){switch(Math.floor(Math.random()*5)){case 0:animateCSS(".homepageLogo","flip");break;case 1:animateCSS(".homepageLogo","heartBeat");break;case 2:animateCSS(".homepageLogo","hinge");break;case 3:animateCSS(".homepageLogo","rubberBand");break;case 4:animateCSS(".homepageLogo","tada");break}}document.addEventListener("DOMContentLoaded",function(){const links=document.querySelectorAll(".scroller a");links.forEach(function(link){link.addEventListener("click",function(e){e.preventDefault();const target=this.getAttribute("href");const targetElement=document.querySelector(target);if(targetElement){const offsetTop=targetElement.getBoundingClientRect().top+window.pageYOffset;const duration=500;const startTime=performance.now();function scrollAnimation(currentTime){const elapsedTime=currentTime-startTime;const progress=Math.min(elapsedTime/duration,1);const easeFunction=progress=>progress;window.scrollTo(0,window.scrollY+easeFunction(progress)*(offsetTop-window.pageYOffset));if(elapsedTime<duration){requestAnimationFrame(scrollAnimation)}}requestAnimationFrame(scrollAnimation)}})})});
